let dp = Array(cost.length).fill().map(() => Array(cost[0].length).fill(Infinity))
dp[0][0] = cost[0][0]
for (let i = 1; i < m; i++) {
    dp[i][0] = dp[i-1][0] + cost[i][0]
}
for (let i = 1; i < n; i++) {
    dp[0][i] = dp[0][i-1] + cost[0][i]
}
for (let i = 1; i < m; i++) {
    for (let j = 1; j < n; j++) {
        dp[i][j] = Math.min(dp[i-1][j], dp[i][j-1]) + cost[i][j]
    }
}
return dp[m-1][n-1]
